import{CartController}from"./CartController.js";const ProductsGalleryController=(t,r)=>{const n=document.getElementById("gallery"),l=t,c=CartController(r),e=(t,r,n,l)=>`<div class="swiper-slide">\n        <article class="product-card">\n            <div class="productthumb">\n                <img\n                    class="product"\n                    src=${l}\n                    alt=${r}\n                />\n            </div>\n            <div class="product-label">\n                <button class="buttoncart" id=${t} >\n                    <i class="fa-solid fa-cart-plus"></i>\n                </button>\n                <h4 class="title">${r}</h4>\n                <span class="price">${n.toLocaleString("pt-br",{style:"currency",currency:"BRL"})}</span>\n            </div>\n        </article>\n    </div>`,o=()=>{const t=Array.from(document.querySelectorAll(".buttoncart"));for(let n of t)n.addEventListener("click",()=>{const t=l.find(t=>t.id===+n.id);r.addItem(t),c.createList(r)})};(()=>{for(let t of l){const{id:r,name:l,price:c,img_url:o}=t;n.innerHTML+=e(r,l,c,o)}o()})()};export{ProductsGalleryController};