import Swiper from"https://cdn.jsdelivr.net/npm/swiper@8/swiper-bundle.esm.browser.min.js";const SwiperController=()=>{return{bannerSwiper:new Swiper(".banner-swiper",{slidesPerView:1,spaceBetween:30,loop:!0,pagination:{el:".swiper-pagination",clickable:!0},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}}),productsSwiper:new Swiper(".products-swiper",{slidesPerView:6,spaceBetween:30,pagination:{el:".swiper-pagination",clickable:!0},breakpoints:{320:{slidesPerView:2,spaceBetween:20},480:{slidesPerView:3,spaceBetween:30},640:{slidesPerView:4,spaceBetween:40},1280:{slidesPerView:6,spaceBetween:30}}}),featureProductSwiper:new Swiper(".feature-swiper",{autoHeight:!0,pagination:{el:".swiper-pagination",type:"fraction"},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}})}};export{SwiperController};